package com.syntax.class07;

public class Decrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			//print numbers from 10 to 1
		
   int b=10;
   while (b>=1) {
	   System.out.print(b+" ");
	   b--;
   }
   System.out.println("-----------");
   
   //print number from 100 to 50  
   
   int c=100;
   while(c>=50) {
	   System.out.print(c+" ");
	   c--;
   }
   System.out.println("------------");
   
   //print number from-1 to -10
   
   int d=-1;
   while (d>=-10) {
	   System.out.print(d+" ");
	   d--;
	   
   }
	}

}
